CREATE TABLE user_role
(
  authority  VARCHAR(20)   NOT NULL,
  title      VARCHAR(20)   NULL,
  CONSTRAINT pk_user_role PRIMARY KEY (authority)
);

INSERT INTO user_role (authority, title) VALUES ('USER', 'User');


CREATE TABLE user
(
  id              BIGINT AUTO_INCREMENT  NOT NULL,
  user_id         BINARY(16)             NULL,
  created_at      datetime(3)            NULL,
  updated_at      datetime(3)            NULL,
  role_authority  VARCHAR(20)            NULL,
  mobile          VARCHAR(20)            NULL,
  password        VARCHAR(255)           NOT NULL,
  fingerprint     VARCHAR(255)           NULL,
  CONSTRAINT pk_user PRIMARY KEY (id)
);

ALTER TABLE user
  ADD CONSTRAINT uc_user_mobile UNIQUE (mobile);

ALTER TABLE user
  ADD CONSTRAINT uc_user_userid UNIQUE (user_id);

ALTER TABLE user
  ADD CONSTRAINT fk_user_on_role FOREIGN KEY (role_authority) REFERENCES user_role (authority);

CREATE TABLE tag ( id BINARY(16) NOT NULL, user_id BIGINT NOT NULL, name VARCHAR(50) NOT NULL, created_at datetime(3) NULL, updated_at datetime(3) NULL, CONSTRAINT pk_tag PRIMARY KEY (id) );
CREATE TABLE max_cost ( id BINARY(16) NOT NULL, tag_id BINARY(16) NOT NULL, amount DECIMAL(23, 5) NOT NULL, period VARCHAR(20) NOT NULL, CONSTRAINT pk_max_cost PRIMARY KEY (id) );
ALTER TABLE max_cost ADD CONSTRAINT fk_max_cost_on_tagid FOREIGN KEY (tag_id) REFERENCES tag (id);
CREATE TABLE expense ( id BINARY(16) NOT NULL, tag_id BINARY(16) NOT NULL, amount DECIMAL(23, 5) NOT NULL, created_at datetime(3) NULL, updated_at datetime(3) NULL, CONSTRAINT pk_expense PRIMARY KEY (id) );
ALTER TABLE expense ADD CONSTRAINT fk_expense_on_tagid FOREIGN KEY (tag_id) REFERENCES tag (id);
